              onClick={() => {
                addArr("do");
              }}
            >
              {" "}
              <div class="text-mono">
                <a
                  href="#!"
                  class="ht-tm-element btn btn-outline-primary btn-shadow text-mono"
                >
                  Excatly!
                  {/* ë‹¹ì¥ ì‹œì‘í•´ */}
                </a>
              </div>
            </div>
          </div>
        </div>
      );
