import React from 'react';
import { connect } from 'react-redux';
import Pagination from '../components/Pagination';
import { setCurrentPage } from '../modules/gameList.js';

const PaginationContainer = ({ postsPerPage, totalPosts, setCurrentPage }) => {
    return(
        <Pagination postsPerPage={postsPerPage} totalPosts={totalPosts} setCurrentPage={setCurrentPage}/>
    );
};

export default connect(
    ({ gameList }) => ({
        postsPerPage: gameList.postsPerPage,
        totalPosts: gameList.totalPosts
    }),
    {
        setCurrentPage,
    }
)(PaginationContainer);
