import React from 'react';
import { connect } from 'react-redux';
import Pagination from '../components/Pagination';
import { totalPosts,setCurrenPage,setPostsPerPage } from '../modules/gameList.js';

const PaginationContainer = ({ postsPerPage, totalPosts, setPage }) => {
    return(
        <Pagination postsPerPage={postsPerPage} totalPosts={totalPosts} setPage={setPage}/>
    );
};

export default connect(
    ({ gameList }) => ({
        // <GameList>의 props
        postsPerPage: gameList.postsPerPage,
        totalPosts: gameList.totalPosts
    }),
    {
        setPage,
    }
)(PaginationContainer);
