import { createAction, handleActions } from "redux-actions";
import produce from 'immer';

const SETTINGPAGE = "pagination/SETTINGPAGE";

export const setPage = createAction(SETTINGPAGE);

const initialState = {
    currentPage : 1
};

const pagination = handleActions(
  {
    [SETTINGPAGE]: (state, action) => ({
      currentPage: state.currentPage = action.payload
    }),
  },                                                                     
  initialState,
);

export default pagination;
