import React, {useEffect } from "react";
import { setCurrentPage } from '../modules/pagination';

const Pagination = ({postsPerPage, totalPosts}) => {
  console.log("페이지네이션.js 시작")
  if(totalPosts>0){
      const pageNumbers = [];
      for (let i = 1; i <= Math.ceil(totalPosts / 10); i++) {
        console.log("페이지네이션 포문 시작")
        pageNumbers.push(i);
      }
      console.log("postsPerPage = "+postsPerPage)
      console.log("pageNumbers 페이지수 = "+pageNumbers)
      return (
        <nav className="paginationNav">
          <ul className='pagination'>
            {pageNumbers.map(number => (
              <li key={number} className='page-item'>
                <a onClick={() => setCurrentPage(number)} href='!#' className='page-link'>
                  {number}
                </a>
              </li>
            ))}
          </ul>
        </nav>
      );
    }
  }

export default Pagination;
